import Widget from "../widget";

export default class AnalogClockWidget extends Widget {
	constructor(core, options) {
		/** The default starting size for this widget. */
		const startingSize = 250;

		/** The minimum dimension for this widget. */
		const minDimension = 150;

		super(
			core,
			options,
			{
				minDimension: {
					width: minDimension,
					height: minDimension,
				},
				dimension: {
					width: startingSize,
					height: startingSize,
				},
			},
			{
				fontFamily: "Monospace",
				colors: {
					numberColor: "#000000",
					hourHandColor: "#000000",
					minuteHandColor: "#000000",
					secondHandColor: "#000000",
				},
			}
		);

		this.$tmpCanvas = document.createElement("canvas");
		this.$tmpCanvas.width = startingSize;
		this.$tmpCanvas.height = startingSize;
		this.tmpContext = this.$tmpCanvas.getContext("2d");

		/**
		 * Indicates whether the clock has already been rendered at
		 * the specified size.
		 * @type {Boolean}
		 */
		this.firstRender = true;
	}

	compute() {
		const { width, height } = this.$canvas;
		const { $tmpCanvas } = this;

		$tmpCanvas.width = width;
		$tmpCanvas.height = height;
	}

	onResize() {
		this.compute();
		this.firstRender = true;
	}

	render({ context, width, height }) {
		// Translate to center of canvas initially
		if (this.firstRender) {
			context.translate(width / 2, height / 2);
			this.firstRender = false;
		}

		const size = Math.min(width, height);
		const radius = (size / 2) * 0.9;
		this.drawClock(context, radius);
	}

	getContextMenu() {
		return [
			{
				label: "Set Font",
				onclick: () => this.createFontDialog(),
			},
			{
				label: "Set Colors",
				onclick: () => this.createMultipleColorsDialog(),
			},
		];
	}

	createFontDialog() {
		this.core.make(
			"meeseOS/dialog",
			"font",
			{
				name: this.options.fontFamily,
				controls: ["name"],
			},
			(btn, value) => {
				if (btn === "ok") {
					this.options.fontFamily = value.name;
					this.compute();
					this.saveSettings();
				}
			}
		);
	}

	/**
	 * Creates a color dialog for all of the widget's properties.
	 */
	createMultipleColorsDialog() {
		this.core.make(
			"meeseOS/dialog",
			"multipleColors",
			{
				colors: this.options.colors,
				title: "Set Widget Colors",
			},
			(btn, value) => {
				if (btn === "ok") {
					this.options.colors = value;
					this.compute();
					this.saveSettings();
				}
			}
		);
	}

	/**
	 * Draws the clock widget to the canvas.
	 * @param {CanvasRenderingContext2D} context the canvas context
	 * @param {Number} radius the radius of the clock
	 * @private
	 */
	drawClock(ctx, radius) {
		this.drawFace(ctx, radius);
		this.drawNumbers(ctx, radius);
		this.drawTime(ctx, radius);
	}

	/**
	 * Draws the clock face to the canvas.
	 * @param {CanvasRenderingContext2D} context the canvas context
	 * @param {Number} radius the radius of the clock
	 * @private
	 */
	drawFace(ctx, radius) {
		// The background of the clock
		ctx.beginPath();
		ctx.arc(0, 0, radius, 0, 2 * Math.PI);
		ctx.fillStyle = "white";
		ctx.fill();

		// Styling for the border of the clock
		const circleRadius = radius * 0.95;
		const circleEnding = radius * 1.05;
		const gradient = ctx.createRadialGradient(
			0,
			0,
			circleRadius,
			0,
			0,
			circleEnding
		);
		gradient.addColorStop(0, "#333");
		gradient.addColorStop(0.5, "white");
		gradient.addColorStop(1, "#333");

		// Draw the border of the clock
		ctx.strokeStyle = gradient;
		ctx.lineWidth = radius * 0.1;
		ctx.stroke();

		// The center of the clock
		ctx.beginPath();
		ctx.arc(0, 0, radius * 0.1, 0, 2 * Math.PI);
		ctx.fillStyle = "black";
		ctx.fill();
	}

	/**
	 * Draws the numbers to the canvas.
	 * @param {CanvasRenderingContext2D} context the canvas context
	 * @param {Number} radius the radius of the clock
	 * @private
	 */
	drawNumbers(ctx, radius) {
		const fontSize = radius * 0.15;
		ctx.fillStyle = this.options.colors.numberColor;
		ctx.font = `${fontSize}px ${this.options.fontFamily}`;
		ctx.textBaseline = "middle";
		ctx.textAlign = "center";

		for (let num = 1; num < 13; num++) {
			const ang = (num * Math.PI) / 6;
			ctx.rotate(ang);
			ctx.translate(0, -radius * 0.85);
			ctx.rotate(-ang);
			ctx.fillText(num.toString(), 0, 0);
			ctx.rotate(ang);
			ctx.translate(0, radius * 0.85);
			ctx.rotate(-ang);
		}
	}

	/**
	 * Draws the time hands to the canvas.
	 * @param {CanvasRenderingContext2D} context the canvas context
	 * @param {Number} radius the radius of the clock
	 * @private
	 */
	drawTime(ctx, radius) {
		const now = new Date();
		let hour = now.getHours() % 12;
		let minute = now.getMinutes();
		let second = now.getSeconds();

		// Hour hand
		ctx.strokeStyle = this.options.colors.hourHandColor;
		hour =
			(hour * Math.PI) / 6 +
			(minute * Math.PI) / (6 * 60) +
			(second * Math.PI) / (360 * 60);
		this.drawHand(ctx, hour, radius * 0.5, radius * 0.07);

		// Minutes hand
		ctx.strokeStyle = this.options.colors.minuteHandColor;
		minute = (minute * Math.PI) / 30 + (second * Math.PI) / (30 * 60);
		this.drawHand(ctx, minute, radius * 0.8, radius * 0.07);

		// Second hand
		ctx.strokeStyle = this.options.colors.secondHandColor;
		second = (second * Math.PI) / 30;
		this.drawHand(ctx, second, radius * 0.9, radius * 0.02);
	}

	/**
	 * Draws a single specified hand to the canvas.
	 * @param {CanvasRenderingContext2D} context the canvas context
	 * @param {Number} radius the radius of the clock
	 * @private
	 */
	drawHand(ctx, pos, length, width) {
		// IDEA: Try to override the default widget behavior and get a
		// smooth animation like https://codepen.io/rkosak/pen/mwyRLK
		ctx.beginPath();
		ctx.lineWidth = width;
		ctx.lineCap = "round";
		ctx.moveTo(0, 0);
		ctx.rotate(pos);
		ctx.lineTo(0, -length);
		ctx.stroke();
		ctx.rotate(-pos);
	}

	static metadata() {
		return {
			...super.metadata(),
			title: "Analog Clock",
		};
	}
}

