const path = require("path");
const maxAge = 60 * 60 * 12;
const mb = (m) => m * 1024 * 1024;

const defaultConfiguration = {
	development: !(process.env.NODE_ENV || "").match(/^prod/i),
	logging: true,
	index: "index.html",
	// NOTE: You will likely need to override `localhost` on Windows machines
	bind: "0.0.0.0",
	port: 8000,
	public: null,
	morgan: "tiny",
	express: {
		maxFieldsSize: mb(20),
		maxFileSize: mb(200),
		maxBodySize: "100kb",
	},
	https: {
		enabled: false,
		options: {
			key: null,
			cert: null,
		},
	},
	ws: {
		port: null,
		ping: 30 * 1000,
	},
	proxy: [
		/*
		{
			source: 'pattern',
			destination: 'pattern',
			options: {}
		}
		*/
	],
	auth: {
		vfsGroups: [],
		defaultGroups: [],
		requiredGroups: [],
		requireAllGroups: false,
		denyUsers: [],
	},
	mime: {
		filenames: {
			// 'filename': 'mime/type'
			Makefile: "text/x-makefile",
			".gitignore": "text/plain",
		},
		define: {
			// 'mime/type': ['ext']
			"text/x-lilypond": ["ly", "ily"],
			"text/x-python": ["py"],
			"application/tar+gzip": ["tgz"],
		},
	},
	session: {
		store: {
